mapper.update:
update tb_bucket
set used_size = used_size + #{fileSize}
where id = #{id}

mapper.select:
select

from tb_bucket
where uid = #{userId}
order by create_time desc
limit #{page},#{size}

select

from tb_bucket b
where b.id in (
    select bid from tb_bucket_privilege
    where uid = #{userId}
)
order by create_time desc
limit #{page},#{size}
